import './HomePageBody.css';
import React, { useState, useEffect } from 'react';
import CircleCard from './Card/CircleCard';
import Inner from './Card/Inner';
import community from './image/community.svg';
import weeks from './image/weeks.svg';
import Carousel from './Carousel';

const weekNewsContent = () => {
    const [selectedDay,setSelectedDay]=useState(null);

    const handleDayClick = (day) => {
        setSelectedDay(day);
    };

    const getDayStyle = (day) => {
        return{
            cursor:'pointer',
            textDecoration: selectedDay === day ? 'underline' : 'none',
            color:selectedDay == day ? 'blue' : 'rgb(153, 153, 153)'
        };
    };
}

export default function HomePageBody() {
    /*const [carouselData,setCarouselData]=useState([]);

    useEffect(()=>{
        fetch('')
        .then((response) => response.json())
        .then((data) => setCarouselData(data))
        .catch((error) => console.error('Error fetching carousel data:', error));
    },[]);*/

    return(
    <div className="body"> 
        <div className="container">
            <div className="mainContent">
                <div className="carouselRec">
                    <div className="carouselImage">
                    {/*{carouselData.length > 0 ? (
                    <Carousel images={carouselData} />
                ) : (
                    <p>Loading...</p>
                )}*/}
                    <Carousel/>
                    </div>
                </div>
                <section className="weekNewsRec">
                    <div className="weekNewsInner">
                        <div className="weekNewsTitle">
                        <CircleCard src={weeks} alt={"weeks"}  href="https://www.baidu.com">
                        每周新番
                        </CircleCard>
                        </div>
                        <div className="weekNewsContent">
                            <div className="weekNavTabs">
                                <div className="weekNav">
                                    <div className="weekNavItem">
                                        <div className="Monday">周一</div>
                                    </div>
                                    <div className="weekNavItem">
                                        <div className="Tuesday">周二</div>
                                    </div>
                                    <div className="weekNavItem">
                                        <div className="Wednesday">周三</div>
                                    </div>
                                    <div className="weekNavItem">
                                        <div className="Thursday">周四</div>
                                    </div>
                                    <div className="weekNavItem">
                                        <div className="Friday">周五</div>
                                    </div>
                                    <div className="weekNavItem">
                                        <div className="Saturday">周六</div>
                                    </div>
                                    <div className="weekNavItem">
                                        <div className="Sunday">周日</div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
            <div className="sideContent">
                <section className="communityRec">
                    <Inner>
                        <CircleCard src={community} alt={"community"} href="https://www.baidu.com">
                        社区讨论
                        </CircleCard>
                    </Inner>
                </section>
            </div>
        </div>
    </div>
           
    );
}
